const Layout = styled.div`
  display: flex;
  align-items: center;
  position: relative;
  z-index: 1;
  justify-content: center;
  .tip-wrapper {
    position: absolute;
    right: -300px;
  }
`;

const Wrapper = styled.div`
  padding: 16px 0px;
  font-size: 16px;
  font-weight: 500;
  line-height: 22px;
  letter-spacing: 0em;
  color: #979abe;
  position: relative;
  display: flex;
  gap: 16px;
  align-items: center;
  justify-content: center;
  right: 0px;
  height: 42px;
  bottom: 0;
  .highlight {
    text-decoration: underline;
    font-weight: 700;
    color: #979abe;
  }

  .button-dark {
    width: 40px;
    height: 23px;
    border-radius: 20px;
    border: 1px solid #979abe;
    position: relative;
    flex-shrink: 0;
    cursor: pointer;
    background: #181a27;
    .button-dark-circle {
      width: 19px;
      height: 19px;
      border-radius: 100%;
      background: #979abe;

      position: absolute;
      left: 2px;
      top: 1px;
    }
  }

  .button-light {
    flex-shrink: 0;
    width: 40px;
    height: 23px;
    border-radius: 20px;
    border: 1px solid #979abe;
    position: relative;
    background: linear-gradient(90deg, #eef3bf 7.5%, #e9f456 100%);
    cursor: pointer;
    .button-light-circle {
      position: absolute;
      right: 2px;
      top: 1px;
      width: 19px;
      height: 19px;
      border-radius: 100%;
      background: #332c4b;
    }
  }
`;

State.init({
  add: false,
  storeShowTip: false,
});

const { add, onChangeAdd, source, hide } = props;

const onAdd = () => {
  Storage.set("storeCheckTip", 1);
  State.update({
    storeShowTip: false,
  });
  onChangeAdd(true);
};

const onCancel = () => {
  Storage.set("storeCheckTip", 1);
  State.update({
    storeShowTip: false,
  });
  onChangeAdd(false);
};

const storeCheckTip = Storage.get(
  "storeCheckTip",
  "guessme.near/widget/ZKEVMWarmUp.add-to-quest-card"
);

if (storeCheckTip === undefined) {
  State.update({
    storeShowTip: true,
  });
  Storage.set("storeCheckTip", 1);
}

if (hide) return <div />;

return (
  <Layout>
    <Wrapper>
      <div>
        Sync to ZkEvm Warm up
        <a className="highlight" href="/warmup">
          [My Quest]
        </a>{" "}
        after transaction
      </div>

      {!add && (
        <div className="button-dark" onClick={onAdd}>
          <div className="button-dark-circle"></div>
        </div>
      )}

      {add && (
        <div className="button-light" onClick={onCancel}>
          <div className="button-light-circle"></div>
        </div>
      )}
      {!add && source !== "quest-card" && state.storeShowTip && (
        <div className="tip-wrapper">{addToQuestTip}</div>
      )}
    </Wrapper>
  </Layout>
);
